    const {title, description, location, date, image,user} = req.body;

    //validations
    if(!title && title.trim()==="" &&
        !description && 
        description.trim()==="" &&
        !location && location.trim()==="" &&
        !date && !user && !image && image.trim()==="")
        {
            return res.status(422).json({message: "Invalid Data"});
        }

        let existingUser;
